library(chemspiderapi)

context("get_record_id_image")

test_that("get_record_id_image() fails if no record_id is provided.", {
  expect_error(
    get_record_id_image()
  )
})

test_that("get_record_id_image() fails if a NULL record_id is provided.", {
  expect_error(
    get_record_id_image(record_id = NULL)
  )
})

test_that("get_record_id_image() fails if a record_id is not a numeric vector.", {
  expect_error(
    get_record_id_image(record_id = "record_id")
  )
})

test_that("get_record_id_image() fails if multiple record_ids are provided.", {
  expect_error(
    get_record_id_image(record_id = c("123", "456"))
  )
})

test_that("get_record_id_image() fails if no API key is provided.", {
  expect_error(
    get_record_id_image(record_id = 2424L)
  )
})

test_that("get_record_id_image() fails if NULL is provided as API key.", {
  expect_error(
    get_record_id_image(record_id = 2424L, apikey = NULL)
  )
})

test_that("get_record_id_image() fails if more than one API key is provided.", {
  expect_error(
    get_record_id_image(record_id = 2424L, apikey = c("API key one", "API key two"))
  )
})

test_that("get_record_id_image() fails if a numeric API key is provided.", {
  expect_error(
    get_record_id_image(record_id = 2424L, apikey = 1234567890)
  )
})

test_that("get_record_id_image() fails if a logical API key is provided.", {
  expect_error(
    get_record_id_image(record_id = 2424L, apikey = TRUE)
  )
})

test_that("get_record_id_image() fails if a non 32-character length API key is provided.", {
  expect_error(
    get_record_id_image(record_id = 2424L, apikey = "abcdefghijklmnopqrstuvqxyz")
  )
})

test_that("get_record_id_image() fails if a non 32-character length API key is provided.", {
  expect_error(
    get_record_id_image(record_id = 2424L, apikey = "abcdefghijklmnopqrstuvqxyz123456", simplify = "wrong")
  )
})

app <- webfakes::new_app()
app$use(webfakes::mw_json())
app$get("/2424/image", function(req, res) {
  res$
    set_status(200L)$
    send(charToRaw("{\"image\":\"\"}"))
})

web <- webfakes::new_app_process(app)

Sys.setenv("GET_RECORD_ID_URL" = web$url())

test_that("get_record_id_image() returns a proper response.", {
  expect_type(
    get_record_id_image(record_id = 2424L,
                       apikey = "abcdefghijklmnopqrstuvqxyz123456",
                       decode = TRUE),
    "list"
  )
})

test_that("get_record_id_image() returns a proper response.", {
  expect_type(
    get_record_id_image(record_id = 2424L,
                       apikey = "abcdefghijklmnopqrstuvqxyz123456",
                       simplify = TRUE),
    "character"
  )
})

Sys.unsetenv("GET_RECORD_ID_URL")

web$stop()
